import React, { Component } from 'react';
import './App.css';

class App extends Component {

constructor(props){
  super(props);


  this.state={
    input:'',
    inputCountry:'',
    city:'',
    country:'',
    temp:0,
    pressure:0,
    wind:0,
    weather:'',
    humidity:0,
    image:'',
    image_url:''
  }

}
//https://openweathermap.org/img/w/{this.state.icon}.png
getWeather = async(e) => {
  e.preventDefault();
  const key="df496e977a8eaf0022cfe8705b872eb3";
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${this.state.input},${this.state.inputCountry}&units=metric&appid=${key}`;
  const api_call= await fetch(url);
  const data = await api_call.json();
  console.log(data);
  this.setState({input:'',inputCountry:''});
  this.setState({city:data.name,
    country:data.sys.country,
    temp:data.main.temp,
    pressure:data.main.pressure,
    humidity:data.main.humidity,
    wind:data.wind.speed,
    weather:data.weather[0].main,
    image:data.weather[0].icon,
    image_url:"https://openweathermap.org/img/w/"+data.weather[0].icon+".png"

  })
  document.getElementsByClassName("output")[0].style.visibility="visible";
}




onChange = (event) => {
    this.setState({ input: event.target.value });

  }
  onChange2 = (event) => {
    this.setState({ inputCountry: event.target.value }); 
  }

  render() {
    return (
      <div id="main">
        <div className="input" style={{margin: "0 auto",height: 220,width: 210}}>
          <form>
            <p style={{  textAlign: "center" }} >Weather Forecast</p>
            <br/>
            <input id="city" type="text" value={this.state.input} name="Inserisci la località" onChange={this.onChange}/>
            <input id="country" type="text" value={this.state.inputCountry} name="Inserisci lo stato" onChange={this.onChange2}/>
            <button type="submit" style={{marginTop: "30px",display: "block",width: 210}} onClick={this.getWeather}>Submit</button>
          </form>
        </div>

        <div className="output">
          <h1>{this.state.city},{this.state.country}</h1>
          <img id="meteo" src={this.state.image_url}/>
          <h3 style={{float:"left"}}>{this.state.temp} °C</h3>
          <h3>{this.state.weather}</h3>

          <h3>Vento: {this.state.wind} m/s</h3>
          <h3>Pressione: {this.state.pressure} HPA</h3>
          <h3>Umidità: {this.state.humidity} %</h3>


        </div>
      </div>
    );
  }
}

export default App;
